//         {
//           username: username,
//           password: password,
//           name: username,
//           email: email,
//           city: city,
//           state: state,
//           zipcode: zipcode,
//           schoolYear: schoolYear,
//           activities: activities,
//           preferences: preferences,
//           isAdmin: false
//         });
//       setIsLoading(false);


//       if (res.status != 200) {
//         setAuthStatus("denied");
//         console.log("denied");
//         return;
//       }
//       try {
//         await AsyncStorage.setItem('user', JSON.stringify(res.data))
//         console.log(res.data);
//       } catch (e) {
