              </View>

              {(isError)?(<Text style={{color:"blue"}}>* Sign up Failed. Please fill in all the blanks.</Text>):<Text/>}


              <View style={styles.btnContainer}>
                <Button color="white" disabled={disabled} title="Create" onPress={null} />
