  header: {
    fontSize: 36,
    fontWeight: "300",
    marginBottom: 48,
    color: "white",
  },
  textInput: {
    height: 40,
    color: "white",
    borderColor: "white",
    borderBottomWidth: 1,
    marginBottom: 50
  },
  btnContainer: {
    marginTop: 12
  }
});
